function Subtotal() {
  return (
    <div className='subtotal'>




      <CurrencyFormat
        renderText={(value) => (
            <>
            <p>
                Subtotal ({basket.length}items)
                 <strong>{`${value}`}</strong>

           </p>
           <small className='subtotal_gift'>
            <input type='checkbox'/>This order contains a gift
            </small>
             </>

        )}
        decimalScale={2}
        value={getBasketTotal(basket)}
        displayType={"text"}
        thousandSeparator={true}
        prefix={"$"}








      />






        </div>
  )
}

export default Subtotal
